import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

interface TemperatureDisplay {
  temperature: FormControl<number>;
  minTemperature: FormControl<number>;
  maxTemperature: FormControl<number>
}

@Component({
  selector: 'app-display',
  templateUrl: './display.component.html',
  styleUrls: ['./display.component.sass']
})
export class DisplayComponent {
  display: FormGroup<TemperatureDisplay> = new FormGroup<TemperatureDisplay>({
    temperature: new FormControl(),
    minTemperature: new FormControl(),
    maxTemperature: new FormControl(),
  });

  constructor(){
    this.display.controls.temperature.setValue(10)
    this.display.valueChanges.subscribe(() => {})
  }



}
<div class="container">
  <div class="container-body-m">
    <app-display-body
      [temperature]="this.display.controls.temperature.value"
      [minTemperature]="this.display.controls.minTemperature.value"
      [maxTemperature]="this.display.controls.maxTemperature.value"
    >
    </app-display-body>
  </div>
  <div class="text">
    <h2>Type the temperatures</h2>
    <p>Type a temperature between -10°C and 70°C</p>
  </div>

  <form [formGroup]="display">
    <input type="number" placeholder="Temperature" formControlName="temperature" />
    <input
      type="number"
      placeholder="Min Temperature"
      formControlName="minTemperature"
    />
    <input
      type="number"
      placeholder="Max Temperature"
      formControlName="maxTemperature"
    />
  </form>
</div>
