import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

interface TemperatureDisplay {
  temperature: FormControl<number | null>;
  minTemperature: FormControl<number | null>;
  maxTemperature: FormControl<number | null>
}

@Component({
  selector: 'app-display',
  templateUrl: './display.component.html',
  styleUrls: ['./display.component.sass']
})
export class DisplayComponent {
  display: FormGroup<TemperatureDisplay> = new FormGroup<TemperatureDisplay>({
    temperature: new FormControl(),
    minTemperature: new FormControl(0),
    maxTemperature: new FormControl(0),
  });

  constructor(){
    this.display.valueChanges.subscribe((formValue) => {
      console.log('MUDOU', formValue)
    })
  }



}
<div class="container">
  <div class="container-body-m">
    <app-display-body
    [temperature]="0"
    [minTemperature]="0"
    [maxTemperature]="0"
    > </app-display-body>
  </div>
  <h2>Type the temperatures</h2>
  <form [formGroup]="display">
    <input type="number" formControlName="temperature" />
    <input type="number" formControlName="minTemperature" />
    <input type="number" formControlName="maxTemperature" />
  </form>
</div>
